    export function createScene(level:Level)
    {

      const hypercube_v1 = new Entity()
      hypercube_v1.setParent(level)
      const gltfShape_2 = new GLTFShape('models/hypercube_v1.glb')
      hypercube_v1.addComponentOrReplace(gltfShape_2)
      const transform_6 = new Transform({
        position: new Vector3(17.5, 4, 17),
        rotation: new Quaternion(0, 0, 0, 1),
        scale: new Vector3(1, 1, 1)
      })
      hypercube_v1.addComponentOrReplace(transform_6)
      engine.addEntity(hypercube_v1)

      const binoculars = new Entity()
binoculars.setParent(level)
const gg = new GLTFShape('models/binoculars.glb')
binoculars.addComponentOrReplace(gg)
const tt = new Transform({
  position: new Vector3(19, 1.5, 5.5),
  rotation: new Quaternion(0, -0.19509032201612828, 0, 0.9807852804032305),
  scale: new Vector3(1, 1, 1)
})
binoculars.addComponentOrReplace(tt)

binoculars.addComponentOrReplace(new OnClick(e=>{
        let dist = Globals.distance(binoculars.getComponent(Transform).position, Globals.camera.position)
        if ( dist < 2)
         {
           
            engine.removeEntity(binoculars)
            const portal = new Entity()
            portal.addComponentOrReplace(Globals.portal)
            portal.addComponentOrReplace(new Transform({
              position: new Vector3(4,1.5,1),
              scale: Vector3.One()
            }))
            portal.setParent(level)
            engine.addEntity(portal)
            portal.addComponentOrReplace(new Animator())

            for(var i = 0; i <= 27; i++)
            {
              log("adding clip")
              portal.getComponent(Animator).addClip(new AnimationState("Polygon_"+i+"|CINEMA_4D_Main|Layer0"))
              log("playing clip")
              portal.getComponent(Animator).getClip("Polygon_"+i+"|CINEMA_4D_Main|Layer0").play()
            }

            for(var i = 1; i <= 27; i++)
            {
              log("adding clip")
              portal.getComponent(Animator).addClip(new AnimationState(("Polygon_"+i+"_2|CINEMA_4D_Main|Layer0")))
              log("playing clip")
              portal.getComponent(Animator).getClip(("Polygon_"+i+"_2|CINEMA_4D_Main|Layer0")).play()
            }
            portal.addComponentOrReplace(new TriggerComponent(new TriggerBoxShape(Vector3.One(),Vector3.Zero()),
            0,null,null,null,()=>{
              portal.removeComponent(TriggerComponent)
              level.events.fireEvent(new Globals.DoTransition())
            },()=>{},false))

            level.events.fireEvent(new Globals.LevelCompleted(level.sceneLevel))
        }
      }))
      engine.addEntity(binoculars)
      



		const blackBox = new Entity()
	  blackBox.setParent(level)
		const gltfShape_10 = new GLTFShape('models/blackBox.glb')
		blackBox.addComponentOrReplace(gltfShape_10)
		const transform_277 = new Transform({
		  position: new Vector3(16, 20.076258605495106, 16),
		  rotation: new Quaternion(0, 0, 0, 1),
		  scale: new Vector3(1, 1, 1)
		})
		blackBox.addComponentOrReplace(transform_277)
    
      hypercube_v1.addComponentOrReplace(transform_6)

      hypercube_v1.addComponentOrReplace(new Animator())

      for(var i = 0; i < 780; i++)
      {
        hypercube_v1.getComponent(Animator).addClip(new AnimationState(("Cube_"+i+"|CINEMA_4D_Main|Layer0")))
        hypercube_v1.getComponent(Animator).getClip(("Cube_"+i+"|CINEMA_4D_Main|Layer0")).play()
      }      
    }

